typedef long long int ll;
const ll mod = ll(1e9)+7ll;
const ll mxval = 1001;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n = nums.size();
        vector<vector<ll>> dp(n,vector<ll>(mxval,0ll)), sum(n,vector<ll>(mxval,0ll));
        for (ll i = n-1ll; i >= 0ll; --i) {
            for (ll val = mxval-1ll; val >= 0ll; --val) {
                if (val > nums[i])
                    continue;
                if (i == n-1ll) {
                    dp[i][val] = 1ll;
                    sum[i][val] = 1ll;
                    if (val != mxval-1ll) sum[i][val] += sum[i][val+1ll];
                    continue;
                }
                ll nxt = val;
                nxt = max(nxt, nums[i+1]-(nums[i]-val));
                if (nxt >= mxval) continue;
                dp[i][val] = sum[i+1][nxt];
                sum[i][val] = sum[i+1][nxt];
                if (val != mxval-1ll) sum[i][val] += sum[i][val+1ll];
                sum[i][val]%=mod;
            }
        }
        debug(dp);
        debug(sum);
        return sum[0][0];
    }
};
