const int M=1e9+7;

class Solution {
public:
int func(int i, int j, vector<int> &nums, vector<vector<int>> &dp){
    if(i==nums.size()) return 1;
    if(dp[i][j]!=-1) return dp[i][j];
    int ans=0;
    for(int val=j;val<51;val++){
        if(nums[i] - val >=0 && nums[i-1] - j >= nums[i]-val){
            ans=(ans + func(i+1,val, nums, dp))%M;
        }
    }
    return dp[i][j]=ans;
}
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n+1, vector<int>(51, -1));
        int ans=0;
        for(int i=0;i<51;i++){
            if(nums[0] - i >=0 )
            ans=(ans + func(1, i , nums, dp))%M;
        }
        return ans;
    }
};
