class Solution {
    public int countOfPairs(int[] values) {
        int size = values.length;
        long[][][] memo = new long[size][51][51];
        for (long[][] layer1 : memo) {
            for (long[] layer2 : layer1) {
                Arrays.fill(layer2, -1);
            }
        }

        return (int) solve(0, 0, values[0], values, memo);
    }

    private long solve(int index, int val1, int val2, int[] values, long[][][] memo) {
        if (index >= values.length) return 1;
        long result = 0;
        long modVal = 1000000007;

        if (memo[index][val1][val2] != -1) return memo[index][val1][val2];

        for (int i = val1; i <= values[index]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[index]) {
                    result += solve(index + 1, i, j, values, memo);
                    result %= modVal;
                }
            }
        }

        memo[index][val1][val2] = result;
        return result;
    }
}
