class Solution {
public:
int dp[2002][52][52];
int mod=1e9+7;
  int fun(vector<int>&nums,int ind,int prev,int prev2){
      if(ind<0) return 1;
        if(dp[ind][prev][prev2]==-1){
            int sum=0;
              for(int i=prev;i>=0;i--){
                if(nums[ind]-i>=prev2){
                    sum=(sum+fun(nums,ind-1,i,nums[ind]-i))%mod;
                }
              }
              dp[ind][prev][prev2]=sum;
        }
        return dp[ind][prev][prev2];
  }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        return fun(nums,n-1,51,0);
    }
};
