class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def dfs(i, prev1):
            if i >= len(nums):
                return 1
            curr_num = nums[i]
            if i > 0:
                prev2 = nums[i-1] - prev1
            else:
                prev2 = inf

            res = 0
            for j in range(prev1, curr_num+1):
                if curr_num - j <= prev2:
                    res += dfs(i + 1, j)
            return res % (10**9+7)

        return dfs(0, 0)
