class Solution {
    int mo = 1000000007;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[][] dp = new long[n][51];
        for(long[] d:dp){
            Arrays.fill(d,-1);
        }
        return (int)dfs(dp,nums,0,0);
    }
    long dfs(long[][] dp, int[] nums, int idx, int pre){
        if(idx == nums.length) return 1;
        long res = 0;
        int cur = idx>0?Math.max(nums[idx]-nums[idx-1],0)+pre:pre;
        for(; cur<=nums[idx] ;cur++){
            if(dp[idx][cur]==-1){
                dp[idx][cur] = dfs(dp,nums,idx+1,cur);
            }
            res = (res + dp[idx][cur])%mo;
        }
        return res;
    }
}
