class Solution {
public:
    const int MOD = 1e9+7;
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n + 1, vector<int>(101, 0)); 

        for (int j = 0; j <= nums[0]; ++j) {
            dp[1][j] = 1;
        }

        for (int i = 2; i <= n; ++i) {
            for (int j = 0; j <= nums[i-1]; ++j) {
                dp[i][j] = 0;
                for (int k = 0; k <= j; ++k) {
                    int arr2_i = nums[i-1] - j;
                    int arr2_prev = nums[i-2] - k;
                    if (arr2_i <= arr2_prev) {  
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
                    }
                }
            }
        }

        int result = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            result = (result + dp[n][j]) % MOD;
        }

        return result;
    }
};
