    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<vector<int>>> dp(n + 1, vector<vector<int>>(51, vector<int>(51, -1)));

        function<int(int, int, int)> solv = [&](int ind, int previ, int prevj) {
            if (ind == n) return 1;
            if (dp[ind][previ][prevj] != -1) return dp[ind][previ][prevj];

            int anshere = 0;
            for (int i = previ; i <= 50; ++i) {
                int j = nums[ind] - i;
                if (j >= 0 && j <= prevj) {
                    anshere = (anshere + solv(ind + 1, i, j)) % MOD;
                }
            }
            return dp[ind][previ][prevj] = anshere;
        };

        return solv(0, 0, 50);
    }
};
