class Solution {
public:
    int m=1e9+7;
    int solve(int i,int x,int y,vector<int>& nums,vector<vector<vector<int>>> &dp)
    {
        int n=dp.size()-1;
        if(i>=n) return 1;
        if(dp[i][x][y]!=-1) return dp[i][x][y];
        dp[i][x][y]=0;
        for(int j=x;j<=nums[i];j++)
        {
            if(nums[i]-j<=y)
                dp[i][x][y]=(dp[i][x][y]+solve(i+1,j,nums[i]-j,nums,dp))%(m);
        }
        return dp[i][x][y];
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>> dp(n+1,vector<vector<int>>(51,vector<int>(51,-1)));
        return solve(0,0,50,nums,dp);
    }
};
