/**
 * @param {number[]} nums
 * @return {number}
 */
let mod = (1e9 + 7);
var countOfPairs = function(nums) {
    const mem = {};

    return helper(nums, 0, -1, 51, mem);
};

var helper = (nums, index, prev1, prev2, mem) => {
    if(index === nums.length) {
        return 1;
    }

    const key = index + "," + prev1 + "," + prev2;
    if(mem[key] !== undefined) {
        return mem[key];
    }

    let ans = 0;
    for(let i = 0; i <= nums[index]; i++) {
        let firstValue = i;
        let secondValue = nums[index] - firstValue;

        if(firstValue >= prev1 && secondValue <= prev2) {
            ans = (ans + helper(nums, index + 1, firstValue, secondValue, mem)) % mod;
        }
    }

    mem[key] = ans;
    return ans;
}
