class Solution {
public:
    int mod = 1e9 + 7;
    int dp[2001][51][51];
    int solve1(int i, int prev1,int prev2, vector<int>& arr1){
    if (i >= arr1.size()) {
        return 1;
    }
    if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
    int ans1 = 0;
    for (int j = prev1; j <= arr1[i]; ++j) {
         if((arr1[i]-j)<=prev2 && (arr1[i]-j)>=0){
             ans1=(ans1%mod+solve1(i+1,j,arr1[i]-j,arr1)%mod)%mod;
         }
    }
    return dp[i][prev1][prev2]=ans1%mod;
}

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve1(0,0,nums[0],nums);
    }
};
