class Solution {
public:
    int countOfPairs(vector<int>& nums) {
    const int MOD = 1e9 + 7;

    int n = nums.size();
    vector<vector<long>> dp(51, vector<long>(51, 0));
    vector<vector<long>> prefix_sum(51, vector<long>(51, 0));

    for (int j = 0; j <= nums[0]; ++j) {
        int k = nums[0] - j;
        dp[j][k] = 1;
    }

    for (int i = 1; i < n; ++i) {
        for (int j_prev = 0; j_prev <= 50; ++j_prev) {
            prefix_sum[j_prev][0] = dp[j_prev][0];
            for (int k_prev = 1; k_prev <= 50; ++k_prev) {
                prefix_sum[j_prev][k_prev] = (prefix_sum[j_prev][k_prev - 1] + dp[j_prev][k_prev]) % MOD;
            }
        }

        vector<vector<long>> new_dp(51, vector<long>(51, 0));
        for (int j = 0; j <= nums[i]; ++j) {
            int k = nums[i] - j;
            for (int j_prev = 0; j_prev <= j; ++j_prev) {
                new_dp[j][k] = (new_dp[j][k] + prefix_sum[j_prev][50] - (k > 0 ? prefix_sum[j_prev][k - 1] : 0) + MOD) % MOD;
            }
        }
        dp = new_dp;  
    }

    int result = 0;
    for (int j = 0; j <= 50; ++j) {
        for (int k = 0; k <= 50; ++k) {
            result = (result + dp[j][k]) % MOD;
        }
    }

    return result;
    }
};
