class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int maxElement=*max_element(nums.begin(),nums.end());
        int mod=1e9+7;

        vector<vector<vector<int>>> dp(n,vector<vector<int>>(maxElement+1,vector<int>(maxElement+1,-1)));
        function<int(int,int,int)> dfs=[&](int i,int arr1Pre,int arr2Pre){
            if(i==n){
                return 1;
            }

            if(dp[i][arr1Pre][arr2Pre]!=-1){
                return dp[i][arr1Pre][arr2Pre];
            }

            int res=0;
            for(int j=arr1Pre;j<=nums[i];j++){
                if(nums[i]-j<=arr2Pre){
                    res=(res+dfs(i+1,j,nums[i]-j))%mod;;
                }else{
                    continue;
                }
            }

            dp[i][arr1Pre][arr2Pre]=res;
            return res;
        };

        return dfs(0,0,maxElement);

    }
};
