class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @lru_cache(None)
        def solve(idx,prev1,prev2):
            if idx==n:
                return 1
            ans=0
            for i in range(prev1,maxval[idx]+1):
                val2=nums[idx]-i
                if val2>=0 and val2<=prev2:
                    #print(idx,i,val2)
                    ans+=solve(idx+1,i,val2)
            # if prev1+prev2==nums[idx]:
            #     ans+=solve(idx+1,prev1,prev2)
            # elif prev1+1+prev2==nums[idx]:
            #     ans+=solve(idx+1,prev1+1,prev2)
            # elif prev1+1+prev2-1==nums[idx]:
            #     ans+=solve(idx+1,prev1+1,prev2-1)
            # elif prev1+prev2+1==nums[idx]:
            #     ans+=solve(idx+1,prev1,prev2+1)
            #print(idx,prev1,prev2,ans)
            return ans%MOD

        MOD=1000000007
        n=len(nums)
        maxval=[0]*n
        maxval[-1]=nums[-1]
        for i in range(n-2,-1,-1):
            maxval[i]=min(nums[i],maxval[i+1])
        return solve(0,0,float('inf'))
