class Solution {
public:
    long long int solve(int idx, int val1, int val2, vector<int>& values, vector<vector<vector<long long int>>>& dp) {
        if (idx >= values.size()) return 1;

        long long int ans = 0;
        long long int M = 1e9 + 7;

        if (dp[idx][val1][val2] != -1) return dp[idx][val1][val2];

        for (int i = val1; i <= values[idx]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[idx]) {
                    ans += solve(idx + 1, i, j, values, dp);
                    ans = ans % M;
                }
            }
        }

        return dp[idx][val1][val2] = ans;
    }

    int countOfPairs(vector<int>& values) {
        int n = values.size();
        vector<vector<vector<long long int>>> dp(n, vector<vector<long long int>>(51, vector<long long int>(51, -1)));
        return solve(0, 0, values[0], values, dp);
    }
};
