#define mod 1000000007
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n+1,vector<int>(51,0));
        for(int i = 0; i<=nums[0]; i++){
            dp[1][i] = 1;
        }
        for(int i = 2; i<=n; i++){
            for(int j = 0; j<=50; j++){
                for(int k = j; k<=50; k++){
                    if(nums[i-1] - j >= nums[i-2] - k){
                        dp[i][j] += dp[i-1][k];
                        dp[i][j] %= mod;
                    }
                }
            }
        }
        int ans = 0;
        for(int i = 0; i<=50; i++){
            ans += dp[n][i];
            ans %= mod;
        }
        return ans;
    }
};
