#define ll long long int

class Solution {
public:
    ll solve(int idx, int fst, int snd, vector<int>& nums, vector<vector<vector<ll>>>& dp) {
        if (idx >= nums.size()) return 1;

        ll x = 0;
        ll mod = 1e9 + 7;

        if (dp[idx][fst][snd] != -1) return dp[idx][fst][snd];

        for (int i = fst; i <= nums[idx]; i++) {
            for (int j = snd; j >= 0; j--) {
                if (i + j == nums[idx]) {
                  x = (x+ solve(idx + 1, i, j, nums, dp)) % mod;
                }
            }
        }



        return (dp[idx][fst][snd] = x);
    }


    int countOfPairs(vector<int>& nums) {
        int nn = nums.size();
        vector<vector<vector<ll>>> dp(nn, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return solve(0, 0, nums[0], nums, dp);

    }
};
