class Solution {
public:
    using ll=long long;
    int mod=1e9+7;

    ll modAdd(ll a, ll b, ll mod){
        return (((a%mod)+(b%mod))%mod);
    }

    ll modSub(ll a, ll b, ll mod){
        return (((a%mod)-(b%mod))%mod);
    }

    ll modMul(ll a, ll b, ll mod){
        return (((a%mod)*(b%mod))%mod);
    }

    int fun(int ind, int prev1, int prev2, vector<int> &nums, vector<vector<vector<int>>> &dp) {
        int n=nums.size();
        if(ind==n){
            return 1;
        }
        if(dp[ind][prev1][prev2]!=-1)return dp[ind][prev1][prev2];
        int ans=0;
        for(int i=0; i<=nums[ind]; ++i) {
            if(i>=prev1 && nums[ind]-i<=prev2){
                ans=modAdd(ans, fun(ind+1, i, nums[ind]-i, nums, dp), mod);
            }
        }
        return dp[ind][prev1][prev2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int ans=0;
        int n=nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>> (51, vector<int> (51, -1)));
        for(int i=0; i<=nums[0]; ++i) {
            ans=modAdd(ans, fun(1, i, nums[0]-i, nums, dp), mod);
        }
        return ans;
    }
};
