#define ll long long
const int mod = 1e9+7;
class Solution {
public:
    int dp[2001][52][52];
    ll solve(int ind,int p1,int p2,int n,vector <int> &nums){
        if(ind==n)return 1;
        if(p1!=-1){
            if(dp[ind][p1][p2]!=-1)return dp[ind][p1][p2]%mod;
        }
        ll ans = 0;
        for(int i=0;i<=nums[ind];i++){
            int a = i;
            int b = nums[ind]-a;
            if(b>=0){
                if(a>=p1 && b<=p2){
                ans+=solve(ind+1,a,b,n,nums)%mod;
                ans%=mod;
                }
            }
        }
        if(p1!=-1){
            dp[ind][p1][p2]=ans%mod;
        }
        return ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        int n = nums.size();
        int p1 = -1,p2=-1;
        ll ans = solve(0,p1,51,n,nums);
        return int(ans);
    }
};
