class Solution:
    def countOfPairs(self, nums: List[int], mod = 10**9+7) -> int:
        if len(nums) == 1:
            return nums[0] + (nums[0] != 0)
        @cache
        def num_pairs(minn=0, maxx=max(nums), i=0):
            print(i)
            if i == len(nums):
                return 1
            total = 0
            for new_minn in reversed(range(minn, nums[i] + 1)):
                new_maxx = nums[i] - new_minn
                if new_maxx > maxx:
                    break
                total += num_pairs(new_minn, new_maxx, i + 1)
                total %= mod
            return total
        return num_pairs()
