class Solution {
    #define ll long long
    ll mod = 1e9+7;
    ll slove(int ind ,vector<int>&nums , int prev,vector<vector<ll>>&dp){
        if(ind == nums.size())
          return 1;
        if(dp[ind][prev] !=-1)
           return dp[ind][prev];
        int ans =0;
        for(int i = prev;i<=nums[ind];i++){
           if(ind == 0 || (nums[ind] - i) <= (nums[ind -1] -prev)){
              ans = (ans+slove(ind+1,nums,i,dp))%mod;
           }
        }
        return dp[ind][prev]= ans%mod;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int maxel = *max_element(nums.begin(),nums.end());
        vector<vector<ll>>dp(n+1,vector<ll>(maxel+1,-1));
        ll ans = slove(0,nums,0,dp)%mod;
        return (int)ans;

    }
};
