#define lli long long int
#define mod 1000000007;
class Solution {
public:
    lli solve( vector<vector<vector<lli>>> & dp, vector<int> & nums, int index, int val1, int val2)
    {
        if(index== nums.size())
            return 1;

        if(dp[index][val1][val2]!=-1)
            return dp[index][val1][val2];

        lli take=0;

        for(int i=0; i<= nums[index]; i++)
        {
            int v1= nums[index]-i;
            int v2= i;

            if(v1>= val1 && v2<= val2)
                take+= solve(dp,nums, index+1, v1, v2);

            take= take % mod;
        }

        return dp[index][val1][val2]= take;


    }

    int countOfPairs(vector<int>& nums) {
        int n= nums.size();
      vector<vector<vector<lli>>> dp(n+1, vector<vector<lli>>(51, vector<lli>(51, -1)));

        lli ans= solve(dp, nums, 0, 0, 50);

        int aa= ans % mod;
        return aa;
    }
};
