class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD=(10**9 + 7)

        @cache
        def fun(i,p1,p2):
            if i==len(nums): return 1
            ans=0
            for curr in range(p1,nums[i]+1):
                if (nums[i]-curr)<=p2:
                    ans+=fun(i+1,curr,nums[i]-curr)
            return ans

        return fun(0,0,50)%MOD
