class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        n = len(nums)
        @cache
        def dfs(i, min1, max2):
            #print(i, min1, max2)
            nonlocal nums
            if i == n:
                return 1
            target = nums[i]
            tempres = 0
            for t2 in range(max2, -1, -1):
                t1 = target - t2
                #print(i, t1, t2, min1, max2)
                if t1 < min1:
                    #print("!!")
                    continue
                tempres += dfs(i + 1, t1, t2)
            return tempres
        return dfs(0, 0, nums[0]) % MOD
