class Solution:
    def countOfPairs(self, nums):
        MOD = 10**9 + 7
        n = len(nums)
        max_val = 50 
        dp = [[0] * (max_val + 1) for _ in range(n)]
        for j in range(nums[0] + 1):
            arr2_val = nums[0] - j
            if arr2_val >= 0:
                dp[0][j] = 1

        for i in range(1, n):
            for j in range(nums[i] + 1):
                arr2_val = nums[i] - j
                if arr2_val >= 0:
                    for k in range(j + 1):
                        if nums[i-1] - k >= arr2_val:
                            dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD

        result = sum(dp[n-1][j] for j in range(nums[n-1] + 1)) % MOD
        return result
