class Solution {
public:
int mod =1e9+7;
    int solve(int i, vector<int>& nums, int prev1, int prev2, vector<vector<vector<int>>>&dp) {

        int n = nums.size();
        if (i >= n) {
            return 1;
        }

        if(dp[i][prev1+1][prev2+1]!=-1){
            return dp[i][prev1+1][prev2+1]%mod;
        }

        int ans = 0;

        if (prev1 == -1) {

            int x = nums[i];

            for (int k = 0; k <= x; k++) {
                ans = (ans%mod + solve(i + 1, nums, k, x - k,dp))%mod;
            }

        } else {

            int x = nums[i];

            for (int k = 0; k <= x; k++) {
                if (k >= prev1 && x - k <= prev2) {
                    ans = (ans%mod + solve(i + 1, nums, k, x - k,dp))%mod;
                }
            }
        }

        return dp[i][prev1+1][prev2+1]= ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n=nums.size();

        vector<vector<vector<int>>>dp(n,vector<vector<int>>(52,vector<int>(52,-1)));

        return solve(0,nums,-1,-1,dp);
    }
};
