class Solution {
public:

    int solve(vector<int>&nums,int idx,int prev,vector<vector<int>>&dp,int mod=1e9+7){
        if(idx>=nums.size())return 1;
        if(dp[idx][prev]!=-1)return dp[idx][prev];
        int ans=0;
        for(int i=prev;i<=nums[idx];i++){
            if(nums[idx]-i>=0 and nums[idx-1]-prev>=0 and ((nums[idx]-i)<=(nums[idx-1]-prev))){
                ans+=solve(nums,idx+1,i,dp);
                ans%=mod;
            }
        }
        return dp[idx][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>>dp(2005,vector<int>(1005,-1));
        int ans=0,mod=1e9+7;
        for(int i=0;i<=nums[0];i++){
            ans+=solve(nums,1,i,dp);
            ans%=mod;
        }
        return ans;
    }
};
