class Solution {
public:
    long long dp[2001][53][53];
    int mod=1e9+7;

    int solve(vector<int>&nums, int i, int lasta, int lastb){
        if(i>=nums.size())return 1;

        if(dp[i][lasta+1][lastb+1]!=-1)return dp[i][lasta+1][lastb+1];

        int ans=0;
        for(int j=0; j<=nums[i]; j++){
            int curra=j, currb=nums[i]-curra;
            if(curra>=lasta && currb<=lastb){
                ans=(ans%mod+solve(nums,i+1,curra,currb)%mod)%mod;
            }
        }

        return dp[i][lasta+1][lastb+1] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof dp);
        return solve(nums,0,-1,51);
    }
};
