class Solution {
public: 
    int dp[2002][51][51];
    int mod = 1e9+7;
    int n;
    int add(int a,int b){
        return ((a%mod)+(b%mod))%mod;
    }
    int mul(int a,int b){
        return ((a%mod)*(b%mod))%mod;
    }
    int rec(int i,int p,int l,vector<int> &nums){
        if(i == n){
            return 1;
        }
        if(dp[i][p][l] != -1){
            return dp[i][p][l];
        }
        int cnt = 0;
        for(int j = 0; j <= 50; j++){
            if(j <= nums[i] && j >= p && nums[i]-j <= l){
               cnt = add(cnt,rec(i+1,j,nums[i]-j,nums));
            }
        }
        return dp[i][p][l] = cnt;
    }
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        for(int i = 0; i <= n; i++){
            for(int j = 0; j <= 50; j++){
                for(int k = 0; k <= 50; k++){
                dp[i][j][k] = -1;
                }
            }
        }
        return rec(0,0,50,nums);
    }
};
