import java.util.Arrays;

class Solution {
	final int M = (1000000007);
    public int countOfPairs(int[] nums) {
    	long[][][] dp = new long[nums.length][51][51];
    	for(int i=0;i<nums.length;i++) {
    		for(int j=0;j<51;j++) {
    			Arrays.fill(dp[i][j], -1);
    		}
    	}
        return (int) (solve(nums, 0, 0, 50, dp));
    }

    private long solve(int[] num, int in, int x, int y, long[][][] dp) {
    	if(in==num.length) return 1;
    	if(dp[in][x][y]!=-1) return dp[in][x][y];
    	long ans = 0;
    	for(int i=x;i<=num[in];i++) {
    		int ny = num[in] - i;
    		if(ny<=y) {
    			ans = (ans + solve(num, in+1, i, ny, dp))%M;
    		}
    	}

    	return dp[in][x][y] = ans;
    }


}
