int dp[2001][51];
class Solution {
public:
    int mod = 1e9 + 7;
    int f(int i, int prev1, int prev2, vector<int>& nums){
    if(i == nums.size()) return 1;
        if(prev1 > nums[i]) return 0;
        if(dp[i][prev1] != -1) return dp[i][prev1];
        int res = 0;
        for(int val1 = 0; val1 <= nums[i]; val1++){
            if(val1 >= prev1 && nums[i] - val1 <= prev2){
                res = (res + f(i + 1, val1, nums[i] - val1, nums)%mod)%mod;
            }
        }
        return dp[i][prev1] = res;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return f(0, 0, 52, nums);
    }
};
