class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long modu = 1000000007;
        long[][] a = new long[n][1002];
        long[][] p = new long[n][1002];
        for (int i=0; i<n; i++)
            for (int j=0; j<=nums[i]; j++) {
                if (i == 0)
                    a[i][j] = 1;
                else {
                    int x = Math.min(j, j-nums[i] + nums[i-1]);
                    if (x >= 0)
                        a[i][j] = p[i-1][x];
                }
                if (j == 0)
                    p[i][j] = a[i][j];
                else
                    p[i][j] = (p[i][j-1] + a[i][j]) % modu;
            }
        return (int) p[n-1][nums[n-1]];
    }
}
