class Solution {
public:
    vector<vector<int>> dp;
    int M = 1e9+7;
    int helper(int i, vector<int> &nums, int n, int prev)
    {
        if(i>=n)
        {
            return 1;
        }
        if(dp[i][prev+1]!=-1) return dp[i][prev+1];
        int ans = 0;
        if(prev==-1)
        {
            for(int l = 0; l<=nums[0]; l++)
            {
                ans = (ans%M+helper(i+1,nums,n,l)%M)%M;
            }
        }
        else
        {
            for(int l = prev; l<=nums[i]; l++)
            {
                int curr = l;
                if(nums[i]-curr>nums[i-1]-prev) continue;
                ans = (ans%M+helper(i+1,nums,n,l)%M)%M;
            }
        }
        return dp[i][prev+1]=ans%M;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.resize(n+1,vector<int>(55,-1));
        return helper(0, nums, n, -1);
    }
};
