#define ll long long int 

class Solution {
public:
   int func (int i,int  p ,vector<int> & nums,
     vector<vector<ll>>  & dp){
           int j,k,n=nums.size();
           if (i==n) return 1;
            if(dp[i][p+1]!=-1) return dp[i][p+1];
            ll mod=1e9+7;
            ll  ans=0;
           for (j=p+1;j<=nums[i];j++){
            //  cout<<j<<" ";
                  ll s=(ll)nums[i]-j;
                   if (s<0) continue;
                   if (i!=0){
                       ll  prev=(ll)nums[i-1]-((ll)p+1);
                       if (prev<0) continue;
                       if (s>prev) continue;
                  }   
                  ans=(ans+func(i+1,j-1,nums,dp)%mod)%mod;
           } 
            return dp[i][p+1]=ans%mod;

   }
    int countOfPairs(vector<int>& nums) {
          int i,j,k,n=nums.size();
           vector<vector<ll>>  dp(n,vector<ll>(51,-1));
           return func (0,-1,nums,dp);
    }
};
