class Solution {
private:
    int mod = 1e9+7;
    int dp[2001][51];
    int f(int i, int p1, vector<int> &nums){
        if(i>=nums.size()) return 1;

        if(dp[i][p1] != -1) return dp[i][p1];

        int ans = 0;
        for(int j=0; j<=nums[i]; j++){
            int c1 = j, c2 = nums[i]-c1;
            int p2 = (i==0)?100:nums[i-1]-p1;
            if(p1<=c1 and p2>=c2){
                ans = (ans%mod + f(i+1,c1,nums)%mod)%mod;
            }
        }

        return dp[i][p1] = ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return f(0,0,nums);
    }
};
