class Solution {
public:

    #define ll long long
    int mod = 1e9 + 7;
    ll solve(vector<int> & nums,int pos,int p1,int p2,vector<vector<vector<ll>>> & dp){

        if(pos == nums.size()) return 1;
        if(dp[pos][p1][p2] != -1) return dp[pos][p1][p2];

        int sum = nums[pos];
        ll curr = 0;
        for(int i=p1;i<=sum; i++){
            int j = sum - i;
            if(j <= p2){
                curr = (curr + solve(nums,pos+1,i,j,dp))%mod;
            }
        }
        return dp[pos][p1][p2] = curr;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<ll>>> dp(n,vector<vector<ll>>(51,vector<ll>(51,-1)));

        ll ans = solve(nums,0,0,50,dp);
        return ans;
    }
};
