class Solution {
    int mod = 1e9 + 7;
public:
    long long solve(vector<int> &nums, long long i ,long long s , long long e, vector<vector<vector<long long>>> &dp){
        if(i == nums.size()){
            // cout<<s<<" "<<e<<endl;
            return 1;
        }
        // cout<<nums[i]<<" "<<s<<" "<<e<<endl;
        // if(nums[i] < s || nums[i] < e) return 0;

        if(dp[i][s][e]!=-1) return dp[i][s][e];

        long long res = 0;
        for(long long k = s; k <= (nums[i]); k++ ){
            if(nums[i] - k > e) continue;
            res += solve(nums, i+1, k, nums[i] - k ,dp);
        }

        return dp[i][s][e] = res%mod;
    }

    int countOfPairs(vector<int>& nums) {
        long long n = nums.size();
        vector<vector<vector<long long>>> dp(n , vector<vector<long long>> (52, vector<long long> (52, -1)));
        return solve(nums, 0, 0, 51, dp);
    }
};
