class Solution {
public:
    const int mod = 1e9+7;
    vector<vector<vector<int>>> dp;

    int fun(vector<int> &v, int ind, int prev1, int prev2) {
        if(ind == v.size()) return 1;

        if(dp[ind][prev1+1][prev2+1] != -1) return dp[ind][prev1+1][prev2+1];

        int val = v[ind];
        int ans = 0;
        for(int i = 0; i <= val; i++) {
            int a = i;
            int b = val - i;

            if((prev1 == -1 && prev2 == -1) || (a >= prev1 && b <= prev2)) {
                ans = (ans + fun(v, ind+1, a, b)) % mod;
            }
        }

        return dp[ind][prev1+1][prev2+1] = ans;
    }

    int countOfPairs(vector<int>& v) {
        int n = v.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(52+2, vector<int>(52+2, -1)));
        return fun(v, 0, -1, -1);
    }
};
