class Solution {
public:
  int mod = 1e9 + 7;
  long long getPairs(int idx, int mini, int maxi, vector<int> &nums,
                     vector<vector<vector<int>>> &memo) {
    if (idx == nums.size())
      return 1;

    if (memo[idx][mini][maxi] != -1)
      return memo[idx][mini][maxi];

    int total = 0;
    for (int i = mini; i <= nums[idx]; i++) {
      if (nums[idx] - i <= maxi) {
        total += getPairs(idx + 1, i, nums[idx] - i, nums, memo);
        total %= mod;
      }
    }

    return memo[idx][mini][maxi] = total % mod;
  }

  int countOfPairs(vector<int> &nums) {
    vector<vector<vector<int>>> memo(
        nums.size(), vector<vector<int>>(51, vector<int>(51, -1)));

    return getPairs(0, 0, 50, nums, memo);
  }
};
