class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        mod =10**9+7
        @lru_cache(None)
        def compute(i,v1,v2):
            if i==n:
                return 1
            res = 0
            # print(i,v1,v2,"call")
            for j in range(51):
                a = j
                b = nums[i]-a
                # print(i,a,b)
                if a >= v1 and b <= v2 and b >= 0 and a >= 0:
                    res += compute(i + 1, a, b)%mod

            return res
        res = compute(0,-1,float('inf'))%mod
        return res
