class Solution {
public:
#define ll long long int
const int mod=1e9+7;
    int count(vector<int>&nums,ll last1,ll last2,ll idx,ll n,vector<vector<vector<ll>>>&dp){
        if(idx>=n)return 1;
        if(dp[idx][last1][last2]==-1){
        ll ways=0;
        ll i=last1;
        ll j=min(last2,1LL*nums[idx]);
        while(i<=nums[idx]&&j>=0){
            if(i+j<nums[idx])
            {
                i++;
            }
            else if(i+j>nums[idx])
            {
                j--;
            }   
            else{
                ways=(ways+count(nums,i,j,idx+1,n,dp))%mod;
                i++;
                j--;
            }
        }
        dp[idx][last1][last2] = ways%mod;
        }
        return dp[idx][last1][last2]%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<ll>>>dp(n+1,vector<vector<ll>>(53,vector<ll>(53,-1)));
        return count(nums,0,51,0,n,dp);
    }
};
