class Solution {
public:

const int MOD = 1e9 + 7;

int recur(int i, int last1, const vector<int>& nums, vector<vector<int>>& dp) {
    if (i == nums.size()) {
        return 1;
    }

    if (dp[i][last1] != -1) {
        return dp[i][last1];
    }

    int res = 0;
    for (int arr1 = last1; arr1 <= nums[i]; ++arr1) {
        int arr2 = nums[i] - arr1;
        if (i == 0 || arr2 <= nums[i-1] - last1) {  
            res = (res%MOD + recur(i + 1, arr1, nums, dp)%MOD) % MOD;
        }
    }

    return dp[i][last1] = res;
}

    int countOfPairs(vector<int>& nums) {
    int n = nums.size();
    vector<vector<int>> dp(n, vector<int>(2001, -1));  
    return recur(0, 0, nums, dp);

    }
};
