class Solution {
public:
    int dp[2002][52][52];
    const int mod=1e9+7;
      int dfs(int ind,int pre1,int pre2,vector<int>&nums){
          if(ind<0) return 1;

          if(dp[ind][pre1][pre2]!=-1) return dp[ind][pre1][pre2];
          int ans=0;
           for(int i=0;i<=nums[ind];i++){
               if(i<=pre1&&nums[ind]-i>=pre2){
                   ans=(ans+dfs(ind-1,i,nums[ind]-i,nums))%mod;
               }
           }

          return dp[ind][pre1][pre2]=ans%mod;
      }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        return dfs(n-1,51,0,nums);
    }
};
