class Solution {
public:

    int mod=1e9+7;
    int dp[20001][51];
    int solve(int i,int prev1,vector<int>&nums){

        int n=nums.size();
        if(i==n)return 1;

        if(dp[i][prev1]!=-1)return dp[i][prev1];

        int curr=0;
        for(int j=prev1;j<=nums[i];j++){


            if( nums[i]-j>=0 && (i==0 || (nums[i]-j<=nums[i-1]-prev1))){
                // cout<<i<<" "<<j<<endl;
                curr=(curr%mod+solve(i+1,j,nums)%mod)%mod;
            }
        }

        return dp[i][prev1]= curr%mod;
    }
    int countOfPairs(vector<int>& nums) {

        int n=nums.size();

        memset(dp,-1,sizeof(dp));


        return solve(0,0,nums);

    }
};
