class Solution {
public:
    int n;
    int dp[2001][52][52];
    const int modi = 1e9 + 7;

    int func(vector<int>& nums, int i, int j, int var) {
        if (i >= n) return 1; 

        int &ans = dp[i][j][var];
        if (ans != -1) return ans;

        ans = 0;

        for (int k = j; k <= nums[i]; k++) {
            for (int g = var; g >= 0; g--) 
            {

                if(k+g==nums[i]) ans += func(nums, i + 1, k, g), ans %= modi;

            }
        }

        return ans;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        memset(dp, -1, sizeof(dp));

        int y = func(nums, 0, 0, nums[0]) % modi;
        return y;
    }
};
