#include <bits/stdc++.h>
using namespace std;
#define dd divyanshudev;
const long long MOD = 1e9 + 7;

class Solution {
public:
    long long solve(int ind, int rishu, int anshu, vector<int>& nums, vector<vector<vector<long long>>>& dp) {
        if (ind >= nums.size()) return 1;
        if (dp[ind][rishu][anshu] != -1) return dp[ind][rishu][anshu];

        long long ans = 0;

        for (int i = rishu; i <= nums[ind]; i++) {
            for (int j = anshu; j >= 0; j--) {
                if (i + j == nums[ind]) {
                    ans =(ans+solve(ind + 1, i, j, nums, dp))%MOD;
                    ans =(ans)%MOD;
                }
            }
        }

        return dp[ind][rishu][anshu] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<long long>>> dp(n, vector<vector<long long>>(56, vector<long long>(56, -1)));
        return solve(0, 0, nums[0], nums, dp);
    }
};
