typedef long long ll;

#define pb push_back
#define sz(x) (int)x.size()
#define all(x) begin(x),end(x)
#define lb(x,y) lower_bound(all(x),y)-begin(x)
class Solution {
public:
    int mod = 1000000007;
    int countOfPairs(vector<int>& nums) {
        int N = sz(nums);
        int prev = 3000;
        vector<int> res(1001, 0), pre(1001, 0);
        res[0] = 1;
        for (int k : nums) {
            pre = res;
            for (int i = 1; i <= 1000; i++) {
                pre[i] += pre[i - 1];
                if (pre[i] >= mod) pre[i] -= mod;
            }
            fill(all(res), 0);
            for (int i = 0; i <= k; i++) {
                int j = k - i;
                int mx = min(i, prev - j);
                if (mx >= 0) res[i] = pre[mx];
            }
            prev = k;
        }
        int res2 = 0;
        for (int i : res) {
            res2 += i;
            if (res2 >= mod) res2 -= mod;
        }
        return res2;
    }
};
