class Solution {
    public int countOfPairs(int[] nums) {
        final int MODE = 1000000007, n = nums.length;
        int m = 0;
        for(int a : nums){
            m = Math.max(m, a);
        }
        final long[][] dp = new long[n][m+1];
        for(int i=0;i<n;++i){
            for(int j=0;j<=m;++j){
                if(j > nums[i]){
                    dp[i][j] = dp[i][j-1];
                }else{
                    if(i == 0){
                        dp[i][j] = 1L;
                    }else{
                        final int k = nums[i] - j;
                        int pre1 = Math.min(nums[i-1]-k, j);
                        if(pre1 >= 0){
                            dp[i][j] = dp[i-1][pre1];
                        }
                    }
                    if(j > 0){
                        dp[i][j] = (dp[i][j] + dp[i][j-1]) % MODE;
                    }
                }
            }
        }
        return (int)dp[n-1][m];
    }
}
