class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = int(1e9 + 7)
        @cache
        def solve(last, idx):
            if idx == n:
                return 1
            ans = 0
            for new_last in range(last, nums[idx]+1):
                if idx > 0 and nums[idx] - new_last > nums[idx-1] - last:
                    continue
                ans = (ans + solve(new_last, idx+1)) % MOD
            return ans
        return solve(0,0)
