class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        max_val = max(nums)

        dp = [[0] * (max_val + 1) for _ in range(n)]
        for x in range(nums[0] + 1):
            dp[0][x] = 1

        for i in range(1, n):
            new_dp = [0] * (max_val + 1)
            for x in range(nums[i] + 1):
                arr2_val = nums[i] - x
                for prev_x in range(x + 1):
                    if arr2_val <= nums[i-1] - prev_x:
                        new_dp[x] = (new_dp[x] + dp[i-1][prev_x]) % MOD
            dp[i] = new_dp

        return sum(dp[n-1]) % MOD
